import React, { useState, useEffect } from "react";
import "./header.css";
import { FaSun, FaMoon } from "react-icons/fa"; // You can use other icon libraries or your custom SVG icons

const Header = () => {
  /*=============== Change Background Header===================*/
  window.addEventListener("scroll", function () {
    const header = document.querySelector(".header");
    if (this.scrollY >= 80) header.classList.add("scroll-header");
    else header.classList.remove("scroll-header");
  });

  /*=============== Toggle Menu ===============*/
  const [Toggle, showMenu] = useState(false);
  const [activeNav, setActiveNav] = useState("#home");
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  useEffect(() => {
    const isDarkMode = localStorage.getItem("darkMode") === "true";
    setDarkMode(isDarkMode);
  }, []);

  useEffect(() => {
    localStorage.setItem("darkMode", darkMode);
    document.body.classList.toggle("dark-mode", darkMode);
  }, [darkMode]);
  return (
    <header className={`header ${darkMode ? "dark-mode" : ""}`}>
      <nav className="nav container">
        <button onClick={toggleDarkMode} className="dark-mode-button">
          {darkMode ? <FaSun /> : <FaMoon />}
        </button>

        <div className={Toggle ? "nav__menu show-menu" : "nav__menu"}>
          <ul className="nav__list grid">
            <li className="nav__item">
              <a
                href="#home"
                onClick={() => setActiveNav("#home")}
                className={
                  activeNav === "#home" ? "navv__link active-link" : "nav__link"
                }
              >
                <i className="uil uil-estate  nav__icon"> </i> Home
              </a>
            </li>

            <li className="nav__item">
              <a
                href="#about"
                onClick={() => setActiveNav("#about")}
                className={
                  activeNav === "#about"
                    ? "navv__link active-link"
                    : "nav__link"
                }
              >
                <i className="uil uil-user nav__icon"> </i> About
              </a>
            </li>

            <li className="nav__item">
              <a
                href="#skills"
                onClick={() => setActiveNav("#skills")}
                className={
                  activeNav === "#skills"
                    ? "navv__link active-link"
                    : "nav__link"
                }
              >
                <i className="uil uil-file-alt nav__icon"> </i> Skills
              </a>
            </li>

            <li className="nav__item">
              <a
                href="#services"
                onClick={() => setActiveNav("#services")}
                className={
                  activeNav === "#services"
                    ? "navv__link active-link"
                    : "nav__link"
                }
              >
                <i className="uil uil-briefcase-alt nav__icon"> </i> Projects
              </a>
            </li>

            <li className="nav__item">
              <a
                href="#qualifications"
                onClick={() => setActiveNav("#qualifications")}
                className={
                  activeNav === "#qualifications"
                    ? "navv__link active-link"
                    : "nav__link"
                }
              >
                <i className="uil uil-scenery nav__icon"> </i> Qualifications
              </a>
            </li>

            <li className="nav__item">
              <a
                href="#contact"
                onClick={() => setActiveNav("#contact")}
                className={
                  activeNav === "#contact"
                    ? "navv__link active-link"
                    : "nav__link"
                }
              >
                <i className="uil uil-message nav__icon"> </i> Contact
              </a>
            </li>
          </ul>

          <i
            className="uil uil-times nav__close"
            onClick={() => showMenu(!Toggle)}
          ></i>
        </div>

        <div className="nav__toggle" onClick={() => showMenu(!Toggle)}>
          <i className="uil uil-apps"></i>
        </div>
      </nav>
    </header>
  );
};

export default Header;
